import {createStore} from "vuex";

export default createStore({
    
    // Само состояние, тут данные
    state: () => ({
        token: '',
        isAuth: false,
    }),

    // computed свойства
    getters: {

    },

    // функции для изменения состояния
    // вызов с помощью $store.commit('func name', param)
    mutations: {
        setToken(state,token){
            state.token = token;
        },
        setIsAuth(state, bool){
            state.isAuth = bool;
        },
    },

    // функции которые юзают мутации
    action: {

    },

})
